/**
 * Cache that stores data in a JSON file.
 */
export class Cache {
    /**
     * @param {string} cacheDir
     * @param {string} name
     */
    constructor(cacheDir: string, name: string);
    _cacheDir: string;
    _cacheFile: string;
    _cache: any;
    /**
     * Read an entry from the cache, or return `null` if the existing entry's
     * version does not match `version`.
     *
     * @param {string} key
     * @param {string} version
     */
    get(key: string, version: string): Promise<any>;
    /**
     * Write an entry to the cache. Writes are not flush to disk until {@link flush}
     * is called.
     *
     * @param {string} key
     * @param {string} version
     * @param {any} value
     */
    set(key: string, version: string, value: any): Promise<void>;
    /**
     * Flush pending cache updates to disk.
     */
    flush(): Promise<void>;
    _readCache(): Promise<any>;
}
