/**
 * Wrap a Rollup bundle configuration to enable selective caching of plugin build hooks.
 *
 * @param {import("rollup").RollupOptions} buildConfig
 * @param {object} options
 *   @param {string} options.cacheRoot
 *   @param {string[]} options.dependencies
 *   @param {string[]} options.cachePlugins
 */
export function addPluginCachingToConfig(buildConfig: import("rollup").RollupOptions, { cacheRoot, dependencies, cachePlugins }: {
    cacheRoot: string;
    dependencies: string[];
    cachePlugins: string[];
}): {
    plugins: any[];
    output?: import("rollup").OutputOptions | import("rollup").OutputOptions[] | undefined;
    acorn?: Record<string, unknown> | undefined;
    acornInjectPlugins?: ((...arguments_: any[]) => unknown)[] | ((...arguments_: any[]) => unknown) | undefined;
    cache?: boolean | import("rollup").RollupCache | undefined;
    context?: string | undefined;
    experimentalCacheExpiry?: number | undefined;
    experimentalLogSideEffects?: boolean | undefined;
    external?: import("rollup").ExternalOption | undefined;
    inlineDynamicImports?: boolean | undefined;
    input?: import("rollup").InputOption | undefined;
    logLevel?: import("rollup").LogLevelOption | undefined;
    makeAbsoluteExternalsRelative?: boolean | "ifRelativeSource" | undefined;
    manualChunks?: import("rollup").ManualChunksOption | undefined;
    maxParallelFileOps?: number | undefined;
    maxParallelFileReads?: number | undefined;
    moduleContext?: ((id: string) => string | import("rollup").NullValue) | {
        [id: string]: string;
    } | undefined;
    onLog?: import("rollup").LogHandlerWithDefault | undefined;
    onwarn?: import("rollup").WarningHandlerWithDefault | undefined;
    perf?: boolean | undefined;
    preserveEntrySignatures?: import("rollup").PreserveEntrySignaturesOption | undefined;
    preserveModules?: boolean | undefined;
    preserveSymlinks?: boolean | undefined;
    shimMissingExports?: boolean | undefined;
    strictDeprecations?: boolean | undefined;
    treeshake?: boolean | import("rollup").TreeshakingPreset | import("rollup").TreeshakingOptions | undefined;
    watch?: false | import("rollup").WatcherOptions | undefined;
};
export type PluginCacheConfig = {
    /**
     * - Directory to write cache files to
     */
    cacheDir: string;
    /**
     * - A version identifier for dependencies of the
     * plugin's output. This is used together with item-specific data to determine
     * whether existing cache entries are valid.
     */
    versionHash: string;
};
